import React, { Component } from 'react'
import { storeProducts } from "./data"
import { isObject } from 'util';
import { BootstrapTable, TableHeaderColumn } from 'react-bootstrap-table';


export default class CustomObj extends Component {
    constructor(props) {
        super(props)
        this.state = {
            store: storeProducts.data,
            tempObjData: ''
        }

        this.textInput = React.createRef();
        this.myRef = React.createRef();
    }

    render() {
        let store = this.state.store;
        var tempObj = {};
        // document.write(store);

        var products = [];

        function addProducts(quantity) {
            const startId = products.length;
            for (let i = 0; i < quantity; i++) {
                const id = startId + i;
                products.push({
                    id: id,
                    name: 'Item name ' + id,
                    price: 2100 + i
                });
            }
            console.log(products);
        }

        addProducts(5)

        Object.keys(store).forEach(function (k) {
            var val = store[k];
            var customer = val['customer_name'];
            var month = val['month'];
            var itemName = val['item_name']
            var salesPerson = val['salesperson_name']

            if (!tempObj[customer])
                tempObj[customer] = { 'type': 'row', 'key': 'customer_name' };

            if (!tempObj[customer][month])
                tempObj[customer][month] = { 'type': 'row', 'key': 'month' }

            if (!tempObj[customer][month][itemName])
                tempObj[customer][month][itemName] = { 'type': 'column', 'key': 'item_name' }

            if (!tempObj[customer][month][itemName][salesPerson])
                tempObj[customer][month][itemName][salesPerson] = { 'type': 'column', 'key': 'sales_person', 'data': { 'value': 0, 'qty': 0 } }

            tempObj[customer][month][itemName][salesPerson]['data']['value'] = val['taxpaidamount'];
            tempObj[customer][month][itemName][salesPerson]['data']['qty'] = val['qty'];

        });

        console.log("result :", tempObj);
        // var tempObjData = JSON.stringify(tempObj)

        // const peopleArray = Object.values(tempObj)
        // var dataArry = peopleArray.map((item, index) => {
        //     return dataArry = item.map((i, j) => {
        //         console.log("i", i)
        //     })
        // })

        // var result = Object.keys(obj).map(function(key) {
        //     return [Number(key), obj[key]];
        // });

        // Start Recursion
        let company = {
            sales: [{
                name: 'John',
                salary: 1000
            }, {
                name: 'Alice',
                salary: 600
            }],

            development: {
                sites: [{
                    name: 'Peter',
                    salary: 2000
                }, {
                    name: 'Alex',
                    salary: 1800
                }],

                internals: [{
                    name: 'Jack',
                    salary: 1300
                }]
            }
        };

        var dataValue = ''
        var dataQty = ''
        var count = 0
        function pivotTablePlot(values) {
            // console.log("start ",values);
            Object.keys(values).forEach(function (k) {
                var subObj = values[k] // we will get new object from here
                console.log("values[k] ", values[k]);
                // if (values[k] == "row") {
                //     let row = this.myRef.insertRow(0);
                //     let cell1 = row.insertCell(0);
                //     let cell2 = row.insertCell(1);
                // }
                // cell1.innerHTML = "NEW CELL1";
                // cell2.innerHTML = "NEW CELL2";
                console.log("k ", k);

                if (values[k].value) {
                    //check if it is a "data" object which have to keys "value" and "qty" 
                    dataValue = dataValue + " + " + values[k].value
                    dataQty = dataQty + " + " + values[k].qty
                }
                else {  // loop through the object to skip 'type' and 'key' keys
                    Object.keys(subObj).forEach(function (itemKey, index) {

                        // console.log("type or key", itemKey)
                        if (typeof subObj[itemKey] == 'object') {
                            console.log("subobject ", subObj[itemKey]);
                            pivotTablePlot(subObj[itemKey]); // recursively call   
                        }
                    })
                }
            })
        }
        pivotTablePlot(tempObj);

        console.log("dataValue", dataValue);
        console.log("dataQty", dataQty);

        // })
        // End recursion 

        var rows = {
            'row1': {
                'cp': { 2: '22', '2': 'fff' },
                'cp2': { 'asd': 'sad' },
            },
            'row2': { 'test': 'pop', 'test2': 1 }
        };


        function iterate(obj, stack) {
            for (var property in obj) {
                if (obj.hasOwnProperty(property)) {
                    if (typeof obj[property] == "object") {
                        iterate(obj[property], stack + '.' + property);
                    } else {
                        // console.log(property + "   " + obj[property]);
                        // $('#output').append($("<div/>").text(stack + '.' + roperty))
                    }
                }
            }
        }

        iterate(rows, '')

        return (
            <>
                {/* {row2} */}
                {/* {row1} */}
                {/* <table ref={this.myRef} border="1" cellPadding="10px">

                </table> */}

                <BootstrapTable data={this.store}>
                    <TableHeaderColumn row='0' rowSpan='2' dataField='customer_name' isKey={true} >Customer</TableHeaderColumn>
                    <TableHeaderColumn row='0' colSpan='2'>Month</TableHeaderColumn>
                    <TableHeaderColumn row='1' dataField='month'>Name</TableHeaderColumn>
                    <TableHeaderColumn row='1' dataField='price'>Price</TableHeaderColumn>
                </BootstrapTable>
            </>
        )
    }
}
