import React, { Component } from 'react'
import { storeProducts } from "./data"

export default class CustomObj extends Component {
    constructor(props) {
        super(props)
        this.state = {
            store: storeProducts.data,
            tempObjData: ''
        }
    }

    render() {
        let store = this.state.store;
        var tempObj = {};

        Object.keys(store).forEach(function (k) {
            var val = store[k];
            var customer = val['customer_name'];
            var month = val['month'];
            var itemName = val['item_name']
            var salesPerson = val['salesperson_name']

            if (!tempObj[customer])
                tempObj[customer] = { 'type': 'row', 'key': 'customer_name' };

            if (!tempObj[customer][month])
                tempObj[customer][month] = { 'type': 'row', 'key': 'month' }

            if (!tempObj[customer][month][itemName])
                tempObj[customer][month][itemName] = { 'type': 'column', 'key': 'item_name' }

            if (!tempObj[customer][month][itemName][salesPerson])
                tempObj[customer][month][itemName][salesPerson] = { 'type': 'column', 'key': 'sales_person', 'data': { 'value': 0, 'qty': 0 } }

            tempObj[customer][month][itemName][salesPerson]['data']['value'] = val['taxpaidamount'];
            tempObj[customer][month][itemName][salesPerson]['data']['qty'] = val['qty'];

        });

        // console.log("result :", tempObj);
        var tempObjData = JSON.stringify(tempObj)

        // console.log(tempObjData) 
        // var arr = Object.values(tempObj);
        var pivotStructure = '';
        Object.keys(tempObj).forEach(function (k) {
            var key = k;
            var value = tempObj[k];
            // console.log(k)
            // console.log(value)
            var value = Object.values(value);
            var valueLength = Object.values(value).length;
            console.log("value ",value);
            console.log("length ",valueLength);

            {
                <table border="1" align="center" cellPadding="10px">
                    <thead>
                        <tr>
                            <th rowSpan="3">Row/column</th>
                        </tr>
                        <tr>
                            <th colSpan="2">Month</th>
                        </tr>
                        <tr>
                            <th>Value</th>
                            <th>Qty</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td rowSpan="2">Monday</td>
                            <td rowSpan="2">8:00 a.m</td>
                            <td rowSpan="2">5:00 p.m</td>
                            <td>Introduction to XML</td>
                        </tr>
                        <tr>
                            <td>Validity: DTD and Relax NG</td>
                        </tr>
                        <tr>
                            <td rowSpan="4">Tuesday</td>
                            <td>8:00 a.m</td>
                            <td>11:00 a.m</td>
                            <td rowSpan="2">XPath</td>
                        </tr>
                        <tr>
                            <td rowSpan="2">11:00 a.m</td>
                            <td rowSpan="2">2:00 p.m</td>
                        </tr>
                        <tr>
                            <td rowSpan="2">XSL transformation</td>
                        </tr>
                        <tr>
                            <td>2:00 p.m</td>
                            <td>5:00 p.m</td>
                        </tr>
                        <tr>
                            <td>Wednesday</td>
                            <td>8:00 a.m</td>
                            <td>12:00 p.m</td>
                            <td>XLS Formatting Objects</td>
                        </tr>

                    </tbody>
                </table>
            }

            let count = Object.keys(value).length //  
            // pivotStructure= `<tr><td rowSpan="2">${key}</td></tr>`

            // console.log(count);



            // var span="";
            // if(value){
            // pivotStructure+=<tr>{value.}</tr>
            // }
            //     span="colspan=10";
            // console.log(value.key)
            // pivotStructure+=
        })


        // console.log(arr)
        // document.write(tempObjData)

        return (
          <>
                <table border="1" align="center" cellPadding="10px">
                    <thead>
                        <tr>
                            <th rowSpan="3">Row/column</th>
                        </tr>
                        <tr>
                            <th colSpan="2">Month</th>
                        </tr>
                        <tr>
                            <th>Value</th>
                            <th>Qty</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td rowSpan="2">Monday</td>
                            <td rowSpan="2">8:00 a.m</td>
                            <td rowSpan="2">5:00 p.m</td>
                            <td>Introduction to XML</td>
                        </tr>
                        <tr>
                            <td>Validity: DTD and Relax NG</td>
                        </tr>
                        <tr>
                            <td rowSpan="4">Tuesday</td>
                            <td>8:00 a.m</td>
                            <td>11:00 a.m</td>
                            <td rowSpan="2">XPath</td>
                        </tr>
                        <tr>
                            <td rowSpan="2">11:00 a.m</td>
                            <td rowSpan="2">2:00 p.m</td>
                        </tr>
                        <tr>
                            <td rowSpan="2">XSL transformation</td>
                        </tr>
                        <tr>
                            <td>2:00 p.m</td>
                            <td>5:00 p.m</td>
                        </tr>
                        <tr>
                            <td>Wednesday</td>
                            <td>8:00 a.m</td>
                            <td>12:00 p.m</td>
                            <td>XLS Formatting Objects</td>
                        </tr>

                    </tbody>
                </table>
          </>
                )
              }
          }
